import React from "react";
import ArtistCard from "./artist-card.js";
class PageSearchResult extends React.Component {
  state = {
    data: {
      similarArtist: {
        artist: [],
      },
    },
  };
  componentDidMount() {
    this.fetchData(
      "https://ws.audioscrobbler.com/2.0/?method=artist.getsimilar&artist=macmiller&api_key=21df39010b423c4f7435c19896c8e1d8&format=json"
    );
  }

  fetchData = async (url) => {
    const response = await fetch(url);
    const data = await response.json();
    console.log(data, "lo que trae la api");
    this.setState({
      data: data,
    });
  };

  render() {
    return (
      <React.Fragment>
        <div className="container">
          <div className="row">
            {this.state.artist.map((artista, i) => {
              return (
                <ArtistCard img={artista.image} titulo={artista.name} key={i} />
              );
            })}
            <h1>{this.props.busqueda}</h1>
          </div>
        </div>
      </React.Fragment>
    );
  }
}

export default PageSearchResult;
