import "./App.css";
import "./page-artist.css";
import React from "react";
import SearchBar from "./Components/search-bar.js";
import SimilarArtist from "./Components/similar-artists.js";
import Loading from "./Components/loading.js";
import Error from "./Components/error.js";

class PageArtist extends React.Component {
  state = {
    busqueda: "",
    data: {
      artist: {},
    },
  };
  handleChange = (e) => {
    this.setState({
      [e.target.name]: e.target.value,
    });
  };

  componentDidMount() {
    this.fetchData(
      "http://ws.audioscrobbler.com/2.0/?method=artist.getinfo&artist=Cher&api_key=21df39010b423c4f7435c19896c8e1d8&format=json"
    );
  }

  fetchData = async (url) => {
    this.setState({
      loading: true,
    });
    const response = await fetch(url);
    const data = await response.json();
    if (data.error) {
      this.setState({
        loading: false,
        error: true,
        errorMensaje: data.message,
      });
    } else {
      this.setState({
        error: false,
        loading: false,
        data: data,
      });
    }
  };

  render() {
    return (
      <React.Fragment>
        <SearchBar
          onChange={this.handleChange}
          busqueda={this.state.busqueda}
        />
        {this.state.loading && <Loading />}
        {this.state.error && <Error errorMensaje={this.state.errorMensaje} />}
        <div className="container">
          <div className="row centrar">
            <div className="col-md-3" />
            <div className="col-md-6">
              <img
                src="https://c-sf.smule.com/rs-s78/arr/59/27/07c4587d-dfab-4e59-9d15-c910626af6c2_1024.jpg"
                alt=""
                className="pic-artist margin30"
              />
              <h2>Gustavo Cerati</h2>
              <p>
                Gustavo Adrián Cerati (Buenos Aires, 11 de agosto de
                1959-ibidem, 4 de septiembre de 2014) fue un músico, cantautor,
                actor, y productor discográfico argentino que obtuvo
                reconocimiento internacional por haber sido el líder de la banda
                de rock Soda Stereo. Es considerado por parte de la prensa,
                cantantes y críticos como uno de los más importantes e
                influyentes del rock latinoamericano.
              </p>
            </div>
          </div>
          <div className="row centrar margin30">
            <SimilarArtist />
          </div>
        </div>
      </React.Fragment>
    );
  }
}

export default PageArtist;
